import db from './firebase';
import React, {useState, useEffect} from 'react';

import Todo from './Todo';

import { Button , FormControl, Input, InputLabel} from '@material-ui/core';
import './App.css';


function App() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState('');


  useEffect(() => {
    //fires when the app loads
    db.collection('todos').onSnapshot(snapshot => { //when something change in the database
      setTodos(snapshot.docs.map(doc => doc.data().todo)) //return a flat array of strings
    })
  }, []);

  const addTodo = (event) => {
    event.preventDefault(); //stop the refresh
    setTodos([...todos, input]) //copy the entire array of todo and append
    setInput('');
  }

  return (
    <div className="App">
      <h1> My To-Do App </h1>

      <form>
        <FormControl>
          <InputLabel> Write a Todo </InputLabel>
          <Input value = {input} onChange = {event => setInput(event.target.value)} />
        </FormControl>

        <Button disabled = {!input} variant="contained" color="primary" type = "submit" onClick = {addTodo} >
          Add Todo
        </Button>
      </form>

      <ul>
        {todos.map(todo => (
          <Todo text = {todo}/>
        ))}
      </ul>

    </div>
  );
}

export default App;
