import './App.css';
import React, {useState} from 'react';

function App() {

  const [todos, setTodos] = useState(['task 1', 'task 2']);

  const [input, setInput] = useState('');

  console.log(input);

  return (
    <div className="App">
      <h1> My To-Do App </h1>

      <input value = {input} onChange = {event => setInput(event.target.value)} >
      </input>

      <button> Add Todo </button>

      <ul>
        {todos.map(todo => (
          <li> {todo} </li>
        ))}
      </ul>

    </div>
  );
}

export default App;
